const initialState = { cocktailData: [] };
const cocktailReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_DATA":
      return { ...state, cocktailData: action.payload };
    case "REMOVE_DATA":
      return {
        ...state,
        cocktailData: [...state.filter((item) => item.id !== action.payload)],
      };
    default:
      return state;
  }
};

export default cocktailReducer;
